import { Link } from "react-router-dom";
import "./searchItem.css";
import useFetch from "../../hooks/useFetch";
importrea

const SearchItem = ({item}) => {
  const images = [
    "https://cf.bstatic.com/xdata/images/xphoto/square300/57584488.webp?k=bf724e4e9b9b75480bbe7fc675460a089ba6414fe4693b83ea3fdd8e938832a6&o=",
    "https://example.com/image2.jpg",
    "https://example.com/image3.jpg",
    "https://example.com/image4.jpg",
    // Add as many images as you want to this array
  ];

  const {data , loading, error} = useFetch("/hotels/countByType");
  console.log(data);

  // Generates a random index to pick a random image from the images array
  const getRandomImageIndex = (prevIndex) => {
    let index;
    do {
      index = Math.floor(Math.random() * images.length);
    } while (index === prevIndex); // ensure no repeat of the previous index
    return index;
  }

  const [prevImageIndex, setPrevImageIndex] = React.useState(-1); // initial value set to -1
  const randomImageIndex = getRandomImageIndex(prevImageIndex);
  setPrevImageIndex(randomImageIndex); // store the current index to use in the next render

  return (
    <div className="searchItem">
      <div className="pList">
        {loading ? ("loading") : (
          <>
            {data &&
              <div className="pListItem" key={1}>
                <img
                  src= {images[randomImageIndex]}
                  alt=""
                  className="pListImg"
                />
              </div>
            }
          </>
        )}
      </div>
      <div className="siDesc">
        <h1 className="siTitle">{item.name}</h1>
        <span className="siDistance">{item.distance} from center</span>
        <span className="siTaxiOp">Free airport taxi</span>
        <span className="siSubtitle">
          Studio Apartment with Air conditioning
        </span>
        <span className="siFeatures">
          {item.desc}
        </span>
        <span className="siCancelOp">Free cancellation </span>
        <span className="siCancelOpSubtitle">
          You can cancel later, so lock in this great price today!
        </span>
      </div>
      <div className="siDetails">
        { item.rating && <div className="siRating">
          <span>Excellent</span>
          <button>{item.rating}</button>
        </div> }
        <div className="siDetailTexts">
          <span className="siPrice">${item.cheapestprice}</span>
          <span className="siTaxOp">Includes taxes and fees</span>
          <Link to={`/hotels/${item._id}`} >
            <button className="siCheckButton">See availability</button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default SearchItem;
