import { useContext, useEffect, useState } from "react"
import Navbar from "../../components/navbar/Navbar";
import {Link} from "react-router-dom"
import {
    
    faLock,
    
    faUser,
  } from "@fortawesome/free-solid-svg-icons";
  import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./login.css"

import jwt_decode from 'jwt-decode'

import axios from "axios"
import { AuthContext } from "../../context/AuthContext"
import {useNavigate} from "react-router-dom"

const Login = () => {
    
    const [use,setUse]=useState({})
    let handleCallbackResponse =(response)=>{
    console.log(response.credential);
    let useObject = jwt_decode(response.credential);
    setUse(useObject);

    document.getElementById('signInDiv').hidden=false
}

useEffect(() =>{
/*global google*/
google.accounts.id.initialize({
  client_id: "1042119384850-8h91gha3qam133v0ik19mrggag0e3e8h.apps.googleusercontent.com",
  callback: handleCallbackResponse
});
google.accounts.id.renderButton(
  document.getElementById("signInDiv"),
  {
    theme:"outline", size:"large" }
);

}, []);

    
   

    const [credentials , setCredentials] = useState({
        username: undefined,
        password: undefined,
    })
    
    const{user, loading, error, dispatch} = useContext(AuthContext)

    const navigate = useNavigate()

    const handleChange = (e)=>{
        setCredentials((prev) => 
        ({
            ...prev, [e.target.id] : e.target.value
        }))
    } 

    const handleClick = async (e)=>{
        e.preventDefault();
        dispatch({type:"LOGIN_START"})
        try{
            const res = await axios.post("/auth/login",credentials);
            dispatch ({type:"LOGIN_SUCCESS",payload : res.data})
            navigate("/");
        }catch(err){
          dispatch({type:"LOGIN_FAIL", payload:err.response.data})
        }
    }
    console.log(use)

  return (
    <body>
      
    <div className="login">
    <Navbar/>
        <div className="lcontainer">
          
        <h1 className="log" id="title">Login</h1>
        <div class="box">
        <FontAwesomeIcon icon={faUser}  className="i"/>
            <input type="text" placeholder="username" id="username"  onChange={handleChange} className="linput" />
            </div>
            <div class="box">
            <FontAwesomeIcon icon={faLock} className="i"/>
            <input type="password" placeholder="password" id="password" onChange={handleChange} className="linput" />
            </div>
            <p>Don't have an account?<a href="#">  <Link to = "/register">
          <button className="navButton">Sign Up
          </button>
          </Link></a></p>
         <button disabled={loading} onClick={handleClick} className="lbtn">log in</button>
         {
            error && 
            <span>{error.message}</span>
         }
            <div className="or">  OR</div>
            <div className="app">
           
              <div id="signInDiv"></div>
             <p> <a href="#">  <Link to = "/">
          <button className="navButton">Login
          </button>
          </Link></a></p>
              
             
           
             
             
            </div>
      

        </div>
    </div>
    </body>
  )
}

export default Login
