import { useContext, useEffect, useState } from "react"
import Navbar from "../../components/navbar/Navbar";
import {
    
    faLock,
    
    faUser,
  } from "@fortawesome/free-solid-svg-icons";
  import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./login.css"

import axios from "axios"
import { AuthContext } from "../../context/AuthContext"
import {useNavigate} from "react-router-dom"

const Login = () => {

    
    let handleCallbackResponse =(response)=>{
          console.log(response)
    }

    useEffect(() =>{
      google.accouns.id.initialize({
        client_id: "807594466309-d537d94bop6cl6r4ipgg5em7h9ligb65.apps.googleusercontent.com",
        callback: handleCallbackResponse
      });
      google.accounts.id.renderButton(
        docunent.getElementById("signInDiv"),
        {
          theme:"outline", size:"large" }
      );
      
    }, []);

    const [credentials , setCredentials] = useState({
        username: undefined,
        password: undefined,
    })
    
    const{user, loading, error, dispatch} = useContext(AuthContext)

    const navigate = useNavigate()

    const handleChange = (e)=>{
        setCredentials((prev) => 
        ({
            ...prev, [e.target.id] : e.target.value
        }))
    } 

    const handleClick = async (e)=>{
        e.preventDefault();
        dispatch({type:"LOGIN_START"})
        try{
            const res = await axios.post("/auth/login",credentials);
            dispatch ({type:"LOGIN_SUCCESS",payload : res.data})
            navigate("/");
        }catch(err){
          dispatch({type:"LOGIN_FAIL", payload:err.response.data})
        }
    }
    console.log(user)

  return (
    <body>
      
    <div className="login">
    <Navbar/>
        <div className="lcontainer">
          
        <h1 id="title">Login</h1>
        <div class="box">
        <FontAwesomeIcon icon={faUser}  className="i"/>
            <input type="text" placeholder="username" id="username"  onChange={handleChange} className="linput" />
            </div>
            <div class="box">
            <FontAwesomeIcon icon={faLock} className="i"/>
            <input type="password" placeholder="password" id="password" onChange={handleChange} className="linput" />
            </div>
            <p>lost password?<a href="#"> Click here</a></p>
         <button disabled={loading} onClick={handleClick} className="lbtn">log in</button>
         {
            error && 
            <span>{error.message}</span>
         }
       
       <div className="app">
        <div id="signInDiv"></div>
        </div>

        </div>
    </div>
    </body>
  )
}

export default Login
